mapper.select:
select 
	ID as id,
	TABLE_NAME_CN as tableNameCn,
	TABLE_NAME as tableName,
	DD_ID_FIELD as ddIdField,
	DD_NAME_FIELD as ddNameField,
	DD_CODE as ddCode,
	IS_CONNECT_STR as isConnectStr,
	COL_1 as col1,
	COL_2 as col2,
	COL_3 as col3,
	COL_4 as col4,
	COL_5 as col5,
	ENABLED as enabled,
	REMOVED as removed,
	CREATE_USER_ID as createUserId,
	CREATE_USER_NAME as createUserName,
	CREATE_TIME as createTime,
	UPDATE_USER_ID as updateUserId,
	UPDATE_USER_NAME as updateUserName,
	UPDATE_TIME as updateTime
from streetcloud.SYS_DATADICTIONARY
where 1=1  

	 and ID = #{id}


	 and TABLE_NAME_CN = #{tableNameCn}


	 and TABLE_NAME = #{tableName}


	 and DD_ID_FIELD = #{ddIdField}


	 and DD_NAME_FIELD = #{ddNameField}


	 and DD_CODE = #{ddCode}


	 and IS_CONNECT_STR = #{isConnectStr}


	 and COL_1 = #{col1}


	 and COL_2 = #{col2}


	 and COL_3 = #{col3}


	 and COL_4 = #{col4}


	 and COL_5 = #{col5}


	 and ENABLED = #{enabled}


	 and REMOVED = #{removed}


	 and CREATE_USER_ID = #{createUserId}


	 and CREATE_USER_NAME = #{createUserName}


	 and CREATE_TIME = #{createTime}


	 and UPDATE_USER_ID = #{updateUserId}


	 and UPDATE_USER_NAME = #{updateUserName}


	 and UPDATE_TIME = #{updateTime}

select ID as id, TABLE_NAME_CN as tableNameCn, TABLE_NAME as tableName, DD_ID_FIELD as ddIdField, DD_NAME_FIELD as ddNameField, DD_CODE as ddCode, IS_CONNECT_STR as isConnectStr, COL_1 as col1, COL_2 as col2, COL_3 as col3, COL_4 as col4, COL_5 as col5, ENABLED as enabled, REMOVED as removed, CREATE_USER_ID as createUserId, CREATE_USER_NAME as createUserName, CREATE_TIME as createTime, UPDATE_USER_ID as updateUserId, UPDATE_USER_NAME as updateUserName, UPDATE_TIME as updateTime from streetcloud.SYS_DATADICTIONARY where 1=1 and ENABLED = 1 and REMOVED = 0
and TABLE_NAME_CN like '%${tableNameCn}%'
and TABLE_NAME like '%${tableName}%'
and DD_ID_FIELD = #{ddIdField}
and DD_NAME_FIELD like '%${ddNameField}%'
and DD_CODE = #{ddCode}
and IS_CONNECT_STR = #{isConnectStr}
order by UPDATE_TIME desc
